import React, { useState } from "react";

import "./ExpensesFilter.css";

const ExpensesFilter = (props) => {

    //state to update the value we get as an input
    const [selectedYear, setSelectedYear] = useState('');

    //handler to change the value of the year and use the state function to update it
    const selectedYearHandler = (event) => {
        console.log('in selecetd')
        console.log(event.target.value);
        setSelectedYear(event.target.value);
        sumbitHandler();
    }

    const sumbitHandler = () => {
        console.log(selectedYear-1);
    }





  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        {/* on change we call the handler */}
        <select onChange={selectedYearHandler}> 
          <option value="2022">2022</option>
          <option value="2021">2021</option>
          <option value="2020">2020</option>
          <option value="2019">2019</option>
        </select>
      </div>
    </div>
  );
};  
export default ExpensesFilter;
