import React,{ useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = (props) => {

  const [enteredTitle, setEnteredTitle] = useState('');
  const [enteredAmount, setEnteredAmount] = useState('');
  const [enteredDate, setEnteredDate] = useState('');
  
  // Multi states into ONE
  // const [userInput, setUserInput] = useState({
  //   enteredTitle: '',
  //   enteredAmount: '',
  //   enteredDate: '',
  // });


  //gets the entire object, and we get the value of it and put it in the state
  //to update the variable
  const titleChangeHandler = (event) => {
    
    setEnteredTitle(event.target.value);

    //better practice to retrieve the previous state's data
    // setUserInput((prevState) => {
    //   return {...prevState enteredTitle: event.target.value};
    // });

  };

  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
  };


  const submitHandler = (event) => {
    //prevents form being sent w/o empty form
    event.preventDefault();

    //putting all that data into a variable
    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate)
    };

    props.onSaveExpenseData(expenseData);
    // console.log(expenseData);

  };  

  //state to handle visiblity of form + button
  const [showForm, setShowForm] = useState(false);
  const [showButton, setShowButton] = useState(true);

  const show = () => {
    setShowForm(!showForm);
    setShowButton(!showButton)
  }


  return (
    <div>
    <button onClick={show}>Add New Expense</button>
    {showForm &&
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          {/* Changes back the value to the initial sate value={enteredTitle} */}
          <input type="text" value={enteredTitle} onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" value={enteredAmount}  min="0.01" step="0.01" onChange={amountChangeHandler}/>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date"  value={enteredDate} min="2019-01-01" max="2022-12-31" onChange={dateChangeHandler}/>
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
    }
    </div>
  );
};

export default ExpenseForm;
