import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import "./Expenses.css";

// When passing any prop other than a string (IN THIS CASE AN ARRAY) to a React component, we have to wrap the value in curly braces.

function Expenses(props) {

    //use state to store 
        //we're using this funciton to pass data back upto the <App/>
        const saveExpenseYearHandler = (enteredExpenseData) =>{

            //new data with an ID passed up from expense form
            const expenseData = {
                ...enteredExpenseData,
                id: Math.random().toString()
            };
            console.log(expenseData);
    
            //passing data up to the <App/> component
            props.onAddExpense(expenseData);
        }

  return (
    // Must have one ROOT element (div)
    <div>
        <ExpensesFilter onSaveYearFilter={saveExpenseYearHandler} />
      <Card className="expenses">
        {/* Here we are using the param in the <Expense items={expenses}/> in App.js to access the array passed into this component thru props*/}
        <ExpenseItem
          title={props.items[0].title}
          amount={props.items[0].amount}
          date={props.items[0].date}
        />
        <ExpenseItem
          title={props.items[1].title}
          amount={props.items[1].amount}
          date={props.items[1].date}
        />
        <ExpenseItem
          title={props.items[2].title}
          amount={props.items[2].amount}
          date={props.items[2].date}
        />
        <ExpenseItem
          title={props.items[3].title}
          amount={props.items[3].amount}
          date={props.items[3].date}
        />
      </Card>
    </div>
  );
}

export default Expenses;
