import React, { useState } from "react";
import Expenses from "./components/Expenses/Expenses";
import NewExpense from "./components/NewExpense/NewExpense";

const INTIAL_EXPENSES = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

const App = () => {
  const [expenses, setExpenses] = useState(INTIAL_EXPENSES);

  //the param we passed into the function inside of <NewExpense> will be the new expense and we can log it her
  const addExpenseHandler = (expense) => {
    setExpenses => {
      return [expense];
    };

    console.log("In App.js");
    console.log(expense);
  };

  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler}></NewExpense>
      {/* You import components into the code by calling the name of the import */}

      {/* i forwarded the array into a param called "props" */}
      <Expenses items={expenses}></Expenses>
    </div>
  );
};

export default App;
